import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: [],
  reducers: {
    addToCart: (state, action) => {
      const { product } = action.payload;
      state = state.map((e) =>
        e.id === product.id
          ? { ...e, amount: e.amount + 1 }
          : { ...e, amount: 1 }
      );
    },
  },
});

export const {
  actions: { addToCart },
  reducer: cartReducers,
} = cartSlice;

export const selectCart = (state) => state.cart;
